class spinnerAction{init(){window.addEventListener("load", ()=>{document.head.insertAdjacentHTML("beforeend", `<style>@keyframes spinner-border{to{transform: rotate(360deg) /* rtl:ignore */ ;}}.spinner-border{display: inline-block; width: 2rem; height: 2rem; vertical-align: -0.125em; border: 0.25em solid currentColor; border-right-color: transparent; border-radius: 50%; animation: 0.75s linear infinite spinner-border;}.spinner-border-sm{width: 1rem; height: 1rem; border-width: 0.2em;}@keyframes spinner-grow{0%{transform: scale(0);}50%{opacity: 1; transform: none;}}.spinner-grow{display: inline-block; width: 2rem; height: 2rem; vertical-align: -0.125em; background-color: currentColor; border-radius: 50%; opacity: 0; animation: 0.75s linear infinite spinner-grow;}.spinner-grow-sm{width: 1rem; height: 1rem;}@media (prefers-reduced-motion: reduce){.spinner-border, .spinner-grow{animation-duration: 1.5s;}}</style>`);let spinners=document.querySelectorAll('[data-action]');for(let spinner of spinners){spinner.addEventListener("click", ({target})=>{let parent=target.closest('[data-action]');let action=parent.getAttribute('data-action'),color=parent.getAttribute('data-color');parent.closest('[data-action]').innerHTML=`<div class="spinner-border ${action=='sm' ? 'spinner-border-sm' : ''}" ${color ? `style="color: ${color}"` : ''}role="status"><span class="sr-only">Loading...</span></div>`});}let glows=document.querySelectorAll('[data-grow]');for(let spinner of glows){spinner.addEventListener("click", ({target})=>{let parent=target.closest('[data-grow]');let action=parent.getAttribute('data-grow'),color=parent.getAttribute('data-color');parent.closest('[data-grow]').innerHTML=`<div class="spinner-grow ${action=='sm' ? 'spinner-grow-sm' : ''}" ${color ? `style="color: ${color}"` : ''}role="status"><span class="sr-only">Loading...</span></div>`});}})}}new spinnerAction().init();
